import sysfrom termcolor import coloredlaptop = {'APPLE MACBOOK AIR': [1345.00, 94.15, 'YES'],          'ASUS S533EQ 15.6': [1448.00, 101.36, 'NO'],          'LENOVO IP 3': [1308.00, 91.56, 'NO']}tablets = {'SAMSUNG 64GB GALAXY TAB': [372.00, 26.04, 'NO'],           'APPLE 10.2-INCH IPAD': [456.00, 31.92, 'YES'],           'HUAWEI HW-BAH3 LTE': [372.00, 26.04, 'YES']}game_console = {'NINTENDO SWITCH CONSOLE': [457.00, 31.99, 'YES'],                'SONY PLAYSTATION 5': [560.00, 39.20, 'NO'],                'MICROSOFT XBOX CONSOLE': [653.00, 45.71, 'YES']}product_list = {**laptop, **tablets, **game_console}p_list = {'APPLE MACBOOK AIR': 0, 'ASUS S533EQ 15.6': 0, 'LENOVO IP 3': 0,          'SAMSUNG 64GB GALAXY TAB': 0, 'APPLE 10.2-INCH IPAD': 0, 'HUAWEI HW-BAH3 LTE': 0,          'NINTENDO SWITCH CONSOLE': 0, 'SONY PLAYSTATION 5': 0, 'MICROSOFT XBOX CONSOLE': 0}def mainMenu():    while True:        print()        print('''### CRYSTAL's COMPUTER SHOP ###              Select a number for the action that you would like to do?                            1. View item list              2. Add item to shopping cart              3. Remove item from shopping cart              4. View items in shopping cart              5. Proceed to check out/payment              0. EXIT              ''')        selection = input("Make your selection: ")        if selection == '1':            display_list()        elif selection == '2':            add_items()        elif selection == '3':            remove_items()        elif selection == '4':            view_items()        elif selection == '5':            payment()        elif selection == '0':            sys.exit()        else:            print('You did not make a valid selection.')# ======================================================================================================================def display_list():    print('Product List')    print('''Sort product by Category, Alpha, or Price    1. Category    2. Alpha    3. Price    4. Main Menu''')    list_selection = input("{a:>28s}".format(a='Please enter your selection: '))    if list_selection == '1':        category_sort()    elif list_selection == '2':        alpha_sort()    elif list_selection == '3':        price_sort()    else:        print('Return main menu. Please enter your selection again: ')        display_list()# =====================================================================================================================def category_sort():    p = 0    list1 = [' ', ' ', 'Laptop', ' ', ' ', 'Tablets', ' ', ' ', 'Game Console', ' ']    print()    print('-'*146)    print('|{:^28}|{:^28}|{:^28}|{:^28}|{:^28}|'.format('Category', 'Brand', 'Price Exclusive GST ($)', 'GST 7% ($)', 'Membership DISC'))    print('='*146)    for k, v in product_list.items():        p += 1        price, GST, membership = v        print('|{:^28}|{:^28}|{:^28}|{:^28}|{:^28}|'.format(list1[p], k, price, GST, membership))        if k == 'LENOVO IP 3' or k == 'HUAWEI HW-BAH3 LTE' or k == 'MICROSOFT XBOX CONSOLE':            print('-'*146)# ======================================================================================================================def alpha_sort():    print()    print('-'*117)    print('|{:^28}|{:^28}|{:^28}|{:^28}|'.format('Brand', 'Price Exclusive GST ($)', 'GST 7% ($)', 'Membership DISC'))    print('='*117)    for k, v in sorted(product_list.items()):        price, GST, membership = v        print('|{:^28}|{:^28}|{:^28}|{:^28}|'.format(k, price, GST, membership))    print('-'*117)# ======================================================================================================================def price_sort():    p = 0    print()    print('-'*117)    print('|{:^28}|{:^28}|{:^28}|{:^28}|'.format('Price Exclusive GST ($)', 'GST 7% ($)', 'Membership DISC', 'Brand'))    print('='*117)    for k, v in product_list.items():        p += 1        price, GST, membership = v        print('|{:^28}|{:^28}|{:^28}|{:^28}|'.format(price, GST, membership, k))        if k == 'LENOVO IP 3' or k == 'HUAWEI HW-BAH3 LTE' or k == 'MICROSOFT XBOX CONSOLE':            print('-'*117)# ======================================================================================================================def add_items():    global p_list    sorted_menu = {}    add_product = {}    p = 0    print('-'*146)    print('|{i:^28}|{a:^28}|{b:^28}|{c:^28}|{d:^28}|'.format(i='No', a='Brand', b='Price Exclusive GST ($)', c='GST 7% ($)', d='Quantity'))    print('='*146)    for i in range(0, len(p_list)):        key = list(p_list.keys())[i]        if p_list[key] > 0:            print('|{i:^28}|{a:^28}| ${b:^28}| ${c:^28}|{d:^28}|'.format(i=p, a=product_list[i]['Category'],                                                                         b=product_list[i]['Price Exclusive GST ($)'],                                                                         c=product_list[i]['GST 7% ($)'], d=p_list[key]))            add_product[p] = product_list[i]['Category']            p += 1    add = input('Please choose your order (A:Return to Sort Menu, B: Return to Main Menu): ')    if add.isdigit():        if (int(add)) >= 0 and int(add) <= 9:            quantity = input('Please indicate quantity: ')            if quantity.isdigit():                if int(quantity) >= 1:                    p_list[sorted_menu[int(add)]['Brand']] += int(quantity)                    print('Item successfully Added!')                else:                    print('Invalid Entry. Please try again.')                    add_items()            else:                print('Invalid Entry. Please try again.')                add_items()        else:            print('Please select only (0 to 9)(A or B) ')            add_items()    elif add == 'A' or add == 'a':        display_list()    elif add == 'B' or add == 'b':        mainMenu()    else:        print('Please select only (0 to 9)(A or B). Please try again.')        add_items()# ======================================================================================================================def remove_items():    global p_list    remove_product = {}    p = 0    non = 0    print('-'*146)    print('|{i:^28}|{a:^28}|{b:^28}|{c:^28}|{d:^28}|'.format(i='No', a='Brand', b='Price Exclusive GST ($)', c='GST 7% ($)', d='Quantity'))    print('='*146)    for i in range(0, len(p_list)):        key = list(p_list.keys())[i]        if p_list[key] > 0:            print('|{i:^28}|{a:^28}| ${b:^28}| ${c:^28}|{d:^28}|'.format(i=p, a=product_list[i]['Category'],                                                                         b=product_list[i]['Price Exclusive GST ($)'],                                                                         c=product_list[i]['GST 7% ($)'], d=p_list[key]))            remove_product[p] = product_list[i]['Category']            p += 1            non += 1    if non == 0:        print("{:^146s}".format('*** No orders placed ***'))    print('-'*146)    remove_selection = input('Choose which order you would like to remove (A:Return to Main Menu): ')    if remove_selection.isdigit():        if (int(remove_selection) < p) and (int(remove_selection) >= 0):            remove_quantity = input('How many item would you like to remove: ')            if remove_quantity.isdigit():                if (int(remove_quantity) >= 1) and (int(remove_quantity) <= p_list[remove_product[int(remove_selection)]]):                    remove_name = remove_product[int(remove_selection)]                    p_list[remove_name] -= int(remove_quantity)                    print('Item Successfully Removed.')                    remove_items()                else:                    print('Invalid Entry. Please try again.')                    remove_items()            else:                print('Invalid Entry. Please try again.')                remove_items()        else:            print('Error!')            print('Please choose items on the list that you would like to remove or A to return Main Menu: ')            remove_items()    elif remove_selection == 'A' or remove_selection == 'a':        mainMenu()    else:        print('Error!')        print('Please choose items on the list that you would like to remove or A to return Main Menu: ')        remove_items()# ======================================================================================================================def view_items():    p = 0    total_p = 0    print('-'*146)    print('|{i:^28}|{a:^28}|{b:^28}|{c:^28}|{d:^28}|{e:^28}|'.format(i='Category', a='Brand', b='Price Exclusive GST ($)', c='GST 7% ($)', d='Quantity', e='Total Price'))    print('='*146)    for i in range(0, len(p_list)):        key = list(p_list.keys())[i]        if p_list[key] > 0:            print('|{i:^28}|{a:^28}| ${b:^28}| ${c:^28}|{d:^28}|{e:^28}|'.format(i=p, a=product_list[i]['Category'],                                                                                 b=product_list[i]['Price Exclusive GST ($)'],                                                                                 c=product_list[i]['GST 7% ($)'], d=p_list[key],                                                                                 e=p_list[key]*product_list[i]['Total Price']))            p += 1            total_p += p_list[key]*product_list[i]['Total Price']    if total_p == 0:        print("{:^146s}".format("***No items placed***"))    print('-'*146)    print('Total Price ${a:<6.2f} (without GST)'.format(a=total_p))# ======================================================================================================================def payment():    GST = 0.07    total_p = 0    member = 0    while True:        membership = input('Are you a member? (YES or NO): ')        membership = membership.upper()        if membership == 'YES':            member = 0.15            break        elif membership == 'NO':            break        else:            print('Error! Please enter YES or NO only.')    print('-'*88)    print('{:^98s}'.format(colored('Shopping Cart', 'cyan')))    print('{:^98s}'.format(colored('Bill Summary', 'red')))    print('-'*88)    print('|{a:^28}|{b:^28}|{c:^28}|'.format(a='Brand', b='Total Price', c='Quantity'))    print('-'*88)    for i in range(0, len(p_list)):        key = list(p_list.keys())[i]        if p_list[key] > 0:            print('|{a:^28}|{b:^28}|{c:^28}|'.format(a=product_list[i]['Brand'], b=p_list[key], c=p_list[key]*product_list[i]['Total Price']))            total_p += p_list[key]*product_list[i]['Total Price']    if total_p == 0:        print("{:^88s}".format("***No items placed***"))    print('-'*88)    print('${a:<6.2f} (Total Price of Items purchased)'.format(a=total_p))    if membership == 'YES':        total_p -= total_p * member        print('${a:<6.2f} (Final Price)'.format(a=total_p))        total_p += total_p * GST        print('${a:<6.2f} (Final Price with GST of 7%)'.format(a=total_p))    if membership == 'NO':        total_p += total_p * GST        print('${a:<6.2f} (Final Price with GST of 7%)'.format(a=total_p))    print('-'*88)# ======================================================================================================================mainMenu()